INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:34:21.116521', '2018-11-23 13:34:21.116537', '阅读器不能开机？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">USB连接电脑或者充电器，检查电池电压是否过低。</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">如果是，请充电一段时间后再使用。</span></p>', 0);
INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:34:46.393280', '2018-11-23 13:34:46.393296', '不能拷贝文件，通过数据线连接电脑没有反应？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">您可以进行如下操作：</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">1）确认计算机与阅读器间是否正确连接或数据线损坏。</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">2）确认存储器的存储空间是否已满。</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">3）阅读器通过数据线连接电脑，需在阅读器页面选择"USB数据传输模式"，阅读器与电脑连接。如果不选择"USB数据传输模式"，阅读器自动进入充电模式。如果阅读通过数据线连接电脑没有弹出选择框，可以选择重新拔插数据线，更换连接电脑USB口或者更换数据线。</span></p>', 0);
INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:35:21.650980', '2018-11-23 13:35:21.650998', '当设备出现死机如何解决？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">如果在操作过程中出现死机，请按一下复位孔的复位键即可恢复。如果电池电量不足请及时充电。</span></p>', 0);
INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:35:50.309858', '2018-11-23 13:35:50.309870', '为什么有时设备待机时间过短？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">聚合物锂电池使用时间会随使用的温度环境及使用情况的不同而有所差异。</span></p>', 0);
INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:36:07.755307', '2018-11-23 13:36:07.755336', '图书和文件无法打开？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">若您下载的书籍无法打开，您可尝试删除文件重新下载书籍到书架，即可阅读；</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">若您通过数据线拷入设备的文档无法打开，您需检查文档格式，因阅读器暂支持Doc、EPUB、fb2、htm、html、mobi、pdb、PDF、prc、rtf、tcr、TXT格式的文档。</span></p>', 0);
INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:36:28.368203', '2018-11-23 13:36:28.368216', '阅读器无法连接WiFi网络？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">可能原因如下：</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">1）检查网络是否开启，在[系统设置]中的[网络设置]，选择开启网络；</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">2）检查WiFi密码是否输入正确，WiFi网络是否有限制；</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">3）如仍无法连接，请重启阅读器，再连接网络。</span></p>', 0);
INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:36:51.114733', '2018-11-23 13:36:51.114745', '如何下载试读书？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">阅读器连接网络，在当当书城根据需要选择书籍，进入书籍单品页面，点击【免费试读】按钮下载试读。（提示：下载试读书籍不需要登录当当账户）</span></p>', 0);
INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:37:13.447263', '2018-11-23 13:37:13.447272', '如何选择购买书籍？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">阅读器连接网络，登录用户账户，在当当书城根据需要选择书籍，进入书籍单品页面，点击购买，在扣除消费金额后，点击下载，即可打开阅读。如果余额不足，请及时到当当网站充值。</span></p>', 0);
INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:37:41.981103', '2018-11-23 13:37:41.981115', '如何在书城查找自己想要的书？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">设备连接网络</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">1）可通过书城推荐，分类选择等方式或细分门类查找自己想要的书籍；</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">2）也可通过进入当当书城，点击上面的搜索框，直接输入书名查找想要的书籍。</span></p>', 0);
INSERT INTO sznewupdate_test.faq (create_time, update_time, question, answer, state) VALUES ('2018-11-23 13:38:05.563021', '2018-11-23 13:38:05.563033', '如何进行系统更新？', '<p><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">本阅读器可实现空中升级（OTA），步骤如下：</span><br style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;"><span style="color: rgb(105, 105, 105); font-family: &quot;Helvetica Neue&quot;, &quot;Lucida Grande&quot;, &quot;Segoe UI&quot;, Arial, Helvetica, Verdana, sans-serif; font-size: 12.8px;">在网络连接后，后台会自动推送更新到终端，如有更新弹出对话框，提示更新，确认后，设备下载升级包；下载完成后进行安装，安装完成后设备将自动重启，完成升级。 您还可以通过“检查更新”，来检查是否有最新的更新可供升级，再根据提示操作。</span></p>', 0);